package day6_operators_boolean;

public class Chars {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char letter='a';
		
		letter++;
		
		System.out.println(letter);
		
		int ascii = letter;
		
		System.out.println(ascii);
		
		char myChar = 'N';
		
		System.out.println(myChar--);
		
		System.out.println(myChar);
	}

}
